import React from 'react';
import { connect } from 'react-redux'
import { setDiscount, setFinalPrice } from '../store';


class Discount extends React.Component {
  constructor(props){
    super(props)

    this.state = {
      input: "",
      validationCode: false,
      validationCodeMessage: "",
      discountPercentage: 0,
      final_price: 0,
      amount_discounted: 0
    }
  }

  handleChange(e){
    this.setState({
      input: e.target.value
    })
  }

  handleClick() {
    const discountPercentage = this.state.discountPercentage;
    const basePrice = this.props.basePrice;
    const {dispatch} = this.props
    switch (this.state.input) {
      case "courseit2019":
        this.setState({
          validationCodeMessage: "C칩digo aplicado!",
          validationCode: true,
          discountPercentage: 10,
          amount_discounted: discountPercentage * basePrice / 100,
          final_price: basePrice - this.state.amount_discounted
        }, () => {dispatch(setDiscount(this.state.discountPercentage), dispatch(setFinalPrice(this.state.final_price)), console.log(this.state, "whole state"))})
        break;
      case "":
        this.setState({
          validationCodeMessage: "Debes escribir un c칩digo",
          validationCode: false
        })
        break;
      default:
        this.setState({
          validationCodeMessage: "C칩digo inv치lido",
          validationCode: false
        })
        break;
    }
  }

  render(){
    return(
      <React.Fragment>
          <label>
            <input className="couponValidationForm" type="text" name="name" placeholder="C칩digo de descuento" onChange={(e) => this.handleChange(e)} />
          </label>
          <button className="button" title="Aplicar" onClick={(e) => this.handleClick(e)} />
          <p>{this.state.validationCodeMessage}</p>
        <style jsx>
        {`
          .button{
            width: 5em;
            height: 4em;
            color: beige;
            font-weight: bold;
            background-color: #157be4;
            border-color: #157be4;
            border-width: 0;
          }
          .couponValidationForm{
            height: 4em;
            border-width: 1px;
            padding: 0 0 0 1em;
          }
        `}
       </style>
      </React.Fragment>
    )
  }
}

function mapStateToProps(state){
  return {
    basePrice: state.productCart.base_price
  }
}

export default connect(mapStateToProps)(Discount);
