import React from 'react'
import { useDispatch } from 'react-redux'
import { addToCart } from '../store'

export default () => {
  const dispatch = useDispatch()

  return (
    <div>
      <button onClick={() => dispatch(addToCart(this.props.product))}>Agregar al carrito</button>
    </div>
  )
}
